INSERT INTO `forums_polls` VALUES (5721,'test poll','a:4:{i:1;s:19:\"Dave is an arsehole\";i:2;s:13:\"Dave is right\";i:3;s:33:\"Who gives a fuck what Dave thinks\";i:4;s:26:\"Polls are a waste of time \";}','2012-07-17 10:10:11','0'),(5735,'why?','a:2:{i:1;s:2:\"no\";i:2;s:5:\"noway\";}','0000-00-00 00:00:00','0'),(5737,'Should there be a thanks button like there is at emp?','a:3:{i:1;s:3:\"Yes\";i:2;s:2:\"No\";i:3;s:10:\"Don\'t Care\";}','2012-07-20 03:46:37','1'),(5956,'What should we do with the [you] tag?','a:6:{i:1;s:109:\"Keep at as it is - anyone can use it - and use the rules like \'no being a dick\' etc. to prevent abuse. (easy)\";i:2;s:40:\"Nuke it, we dont need or want it. (easy)\";i:3;s:86:\"Only let pervs with \'advanced tags\' permission use it - (currently good pervs+) (easy)\";i:4;s:97:\"Only let staff/FLS use it. (time consuming - might be more work than its worth from a coding pov)\";i:5;s:113:\"Keep it but format it differently, perhaps without a link and the tooltip saying \'this is a blah blah tag\' (easy)\";i:6;s:19:\"radioactive monkeys\";}','2012-08-07 23:39:44',''),(6068,'testing','a:2:{i:1;s:28:\"Aaa aaaaaaaaaaaaaa Aaaaaaaa?\";i:2;s:25:\"Bbbbbbbbbb bbbbbbbbb BBB?\";}','0000-00-00 00:00:00','0');
